# cultural_treasure_hunt.py
import streamlit as st
import random

# Cultural treasures in different regions of India
treasures = {
    "North India": ["Taj Mahal", "Jaipur's Hawa Mahal", "Golden Temple"],
    "South India": ["Brihadeeswarar Temple", "Backwaters of Kerala", "Meenakshi Temple"],
    "East India": ["Victoria Memorial", "Konark Sun Temple", "Howrah Bridge"],
    "West India": ["Gateway of India", "Ajanta Caves", "Rann of Kutch"],
    "Central India": ["Khajuraho Temples", "Sanchi Stupa", "Bhimbetka Caves"],
}

def cultural_treasure_hunt():
    st.title("Cultural Treasure Hunt: Discover India")
    st.image("data:image/jpeg;base64,", use_column_width=True)
    st.write("Embark on a virtual journey to uncover hidden cultural treasures in different regions of India!")

    # Select a random region
    region = random.choice(list(treasures.keys()))

    st.subheader("Your quest begins in **{}**. Can you find the cultural treasures hidden here?".format(region))

    # Display the treasures in the selected region
    treasures_in_region = treasures[region]
    user_guess = st.text_input("Enter your guess separated by commas (e.g., Taj Mahal, Hawa Mahal):")

    if set(user_guess.lower().split(", ")) == set(treasures_in_region):
        st.success("Congratulations! You've discovered the hidden treasures in {}! ðŸŒŸ".format(region))
    elif user_guess != "":
        st.error("Oops! Not all treasures were found. Keep exploring!")

    # Allow users to reveal the correct treasures
    if st.button("Reveal Treasures"):
        st.info("The hidden treasures in {} are: {}".format(region, ", ".join(treasures_in_region)))

if __name__ == "__main__":
    cultural_treasure_hunt()
